mapper.update:
update bankbook set 
bookName=#{bookName}, 
bookRate=#{bookRate}, 
bookSale=#{bookSale} 
where bookNumber=#{bookNumber}

mapper.select:
  		SELECT * FROM
    		(SELECT rownum R, BK.* FROM
        		(SELECT * 
         		 FROM BANKBOOK 

         		 ORDER BY BOOKNUMBER desc
         		)
       		 BK
    		)
		WHERE R between #{startRow} and #{lastRow}
